@Composable
fun ClickCaptchaDialog(
    image: String,
    thumbImage: String,
    verifyOnClick: (List<Pair<Float, Float>>) -> Unit = {},
    refreshOnClick: () -> Unit = {},
    onDismissRequest: () -> Unit = {},
    clickPosition: List<Pair<Float, Float>>,
    clickPositionAction: (Offset) -> Unit,
) {
    Dialog(
        onDismissRequest = onDismissRequest,
    ) {
        Box(
            modifier =
                Modifier
                    .background(Color.White),
        ) {
            var siblingHeight by remember { mutableStateOf(0) }
            Column(
                modifier =
                    Modifier.onGloballyPositioned {
                        siblingHeight = it.size.height
                    },
            ) {
                Text(
                    text = "点击验证",
                    fontSize = 20.sp,
                    modifier =
                        Modifier
                            .padding(20.dp),
                )
                Divider(
                    color = Color.Black,
                    modifier = Modifier.padding(start = 10.dp, end = 10.dp),
                )
                Spacer(modifier = Modifier.height(30.dp))
                Row(
                    horizontalArrangement = Arrangement.Center,
                ) {
                    Text(
                        text = "请依次点击:   ",
                        fontSize = 18.sp,
                        modifier = Modifier.padding(start = 20.dp),
                    )
                    Box(modifier = Modifier.size(width = 150.dp, height = 50.dp)) {
                        base64ToImageBitmap(thumbImage)?.let {
                            Image(
                                modifier = Modifier.fillMaxSize(),
                                bitmap = it,
                                contentDescription = null,
                            )
                        }
                    }
                }
                Box(
                    modifier =
                        Modifier
                            .size(320.dp, 180.dp)
                            .padding(start = 10.dp, end = 10.dp),
                ) {
                    base64ToImageBitmap(
                        image,
                    )?.let { CaptchaImage(bitmap = it, clickPosition, clickPositionAction = clickPositionAction) }
                }
                Spacer(modifier = Modifier.height(20.dp))
                Row(
                    horizontalArrangement = Arrangement.SpaceBetween,
                    modifier = Modifier.fillMaxWidth(),
                ) {
                    IconButton(onClick = {
                        refreshOnClick()
                        clickPosition.toMutableList().clear()
                    }) {
                        Icon(Icons.Filled.Refresh, null)
                    }
                    Button(
                        onClick = {
                            verifyOnClick(clickPosition)
                        },
                        modifier =
                            Modifier
                                .widthIn(min = 100.dp)
                                .padding(end = 10.dp),
                    ) {
                        Text(
                            text = "验证",
                            style =
                                TextStyle(
                                    fontSize = 16.sp,
                                    fontWeight = FontWeight(400),
                                    color = Color(0xFFFFFFFF),
                                    textAlign = TextAlign.Center,
                                ),
                        )
                    }
                }
                Spacer(modifier = Modifier.height(20.dp))
            }
        }
    }
}

@Preview
@Composable
fun ClickCaptchaDialogPreview() {
    val thumbImage =
        @Suppress("ktlint:standard:max-line-length")
        "data:image/png;base64,"
    val image =
        @Suppress("ktlint:standard:max-line-length")
        "data:image/jpeg;base64,"

    ClickCaptchaDialog(image, thumbImage = thumbImage, clickPosition = emptyList(), clickPositionAction = {})
}

@Composable
fun CaptchaImage(
    bitmap: ImageBitmap,
    clickPosition: List<Pair<Float, Float>>,
    clickPositionAction: (Offset) -> Unit,
) {
    Image(
        bitmap = bitmap,
        contentDescription = null, // 如果不需要内容描述，可以设为null
        modifier =
            Modifier
                .pointerInput(Unit) {
                    detectTapGestures { offset ->
                        clickPositionAction(offset)
                    }
                }
                .fillMaxSize().aspectRatio(16f / 9f),
    )
    Canvas(modifier = Modifier.fillMaxSize().aspectRatio(16f / 9f)) {
        clickPosition.forEachIndexed { index, (x, y) ->
            // 绘制一个小圆
            drawCircle(Color.Green, radius = 35f, center = Offset(x, y))

            // 在小圆的中心绘制数字
            drawIntoCanvas { canvas ->
                val paint = Paint().asFrameworkPaint()
                paint.textSize = 50f
                paint.color = android.graphics.Color.RED
                paint.textAlign = android.graphics.Paint.Align.CENTER
                val text = (index + 1).toString() // 为了避免显示0，这里使用index + 1
                val xPos = x
                val yPos = y + (paint.textSize / 3)
                canvas.nativeCanvas.drawText(text, xPos, yPos, paint)
            }
        }
    }
}

fun base64ToImageBitmap(base: String): ImageBitmap? {
    if (base != "") {
        val decode: ByteArray = Base64.decode(base.split(",").get(1), Base64.DEFAULT)
        return BitmapFactory.decodeByteArray(decode, 0, decode.size).asImageBitmap()
    }
    return null
}
